// ‰∏§‰∏™ÂèòÈáèÂàÜÂà´ÊòØËÉåÊôØÂÖÉÁ¥†ÁöÑ class„ÄÅÁîüÊàêÁöÑÁÆ≠Â§¥ class
const banner = "banner";
const banner_arrow = "banner-arrow";
export default {
  data() {
    return {
      // ‰∏ãÈù¢ÈÉΩÊòØÈÖçÁΩÆÁöÑÈªòËÆ§ÂÄºÔºåÂª∫ËÆÆÂú® themeConfig ËøõË°åÈÖçÁΩÆÔºåÂÆÉ‰ª¨Â∞ÜË¶ÜÁõñËøô‰∫õÂ±ûÊÄßÂÄº
      navColor: 1,
      switchNavColor: false,
      bgTimeColor: false,
      bgTimeColorArray: [
        "transparent", // ÈÄèÊòé
        "rgba(255, 148, 48, .2)",
        "rgba(0, 0, 0, .3)",
        "rgba(0, 0, 0, .5)",
      ],
      descFade: false,
      desc: [],
      descFadeInTime: 200,
      descFadeOutTime: 100,
      descNextTime: 800,
      descFontSize: "1.4rem",
      bubble: false,
      bubblePosition: 0,
      bubbleNum: 200,
      fadeInInterval: "", // Ê∑°ÂÖ•ÂÆöÊó∂Âô®
      fadeOutInterval: "", // Ê∑°Âá∫ÂÆöÊó∂Âô®
    };
  },
  mounted() {
    const arrow = document.getElementById(banner_arrow);
    arrow && arrow.parentNode.removeChild(arrow);
    let a = document.createElement("a");
    a.id = banner_arrow;
    a.className = banner_arrow;
    document.getElementsByClassName(banner)[0].append(a);
    let targetA = document.getElementById(banner_arrow);
    targetA.addEventListener("click", (e) => {
      // Ê∑ªÂä†ÁÇπÂáª‰∫ã‰ª∂
      this.scrollFn();
    });

    // ÂàùÂßãÂåñÈÖçÁΩÆ
    this.initConfig();

    // ÂàùÂßãÂåñÁªÑ‰ª∂ÂäüËÉΩ
    if (this.bgTimeColor) {
      this.bgTimeColorAndTip();
    }
    setTimeout(() => {
      this.noBgBlur();
    }, 100);

    this.blurText();
    this.watchScroll();

    if (this.descFade) {
      this.textFadeInAndOut();
    }
    if (this.bubble) {
      let canvas = document.createElement("canvas");
      canvas.id = "canvas";
      canvas.style.top = this.bubblePosition + "%";
      document.getElementsByClassName(banner)[0].append(canvas);
      this.canvasBubble();
    }
  },
  watch: {
    $route(to, from) {
      // ÁÇπÂáª‰∏ã‰∏ÄÈ°µÂêéÔºåÂæÄ‰∏ãÊªëÂä®ÔºåÁßªÂá∫Â§ßÂõæ
      if (to.path == "/" && Object.keys(this.$route.query).length > 0) {
        setTimeout(() => {
          this.clickArrow();
        }, 200);
      }
    },
  },
  methods: {
    // ÂàùÂßãÂåñÈÖçÁΩÆ
    initConfig() {
      if (
        this.$themeConfig.indexImg &&
        Object.keys(this.$themeConfig.indexImg).length > 0
      ) {
        this.navColor =
          this.$themeConfig.indexImg.navColor == undefined
            ? this.navColor
            : this.$themeConfig.indexImg.navColor;
        this.switchNavColor =
          this.$themeConfig.indexImg.switchNavColor == undefined
            ? this.switchNavColor
            : this.$themeConfig.indexImg.switchNavColor;
        this.bgTimeColor =
          this.$themeConfig.indexImg.bgTimeColor == undefined
            ? this.bgTimeColor
            : this.$themeConfig.indexImg.bgTimeColor;
        this.bgTimeColorArray =
          this.$themeConfig.indexImg.bgTimeColorArray == undefined
            ? this.bgTimeColorArray
            : this.$themeConfig.indexImg.bgTimeColorArray;
        this.descFade =
          this.$themeConfig.indexImg.descFade == undefined
            ? this.descFade
            : this.$themeConfig.indexImg.descFade;
        this.desc =
          this.$themeConfig.indexImg.desc == undefined
            ? this.desc
            : this.$themeConfig.indexImg.desc;
        this.descFontSize =
          this.$themeConfig.indexImg.descFontSize == undefined
            ? this.descFontSize
            : this.$themeConfig.indexImg.descFontSize;
        this.descFadeInTime =
          this.$themeConfig.indexImg.descFadeInTime == undefined
            ? this.descFadeInTime
            : this.$themeConfig.indexImg.descFadeInTime;
        this.descNextTime =
          this.$themeConfig.indexImg.descNextTime == undefined
            ? this.descNextTime
            : this.$themeConfig.indexImg.descNextTime;
        this.bubble =
          this.$themeConfig.indexImg.bubble == undefined
            ? this.bubble
            : this.$themeConfig.indexImg.bubble;
        this.bubblePosition =
          this.$themeConfig.indexImg.bubblePosition == undefined
            ? this.bubblePosition
            : this.$themeConfig.indexImg.bubblePosition;
        this.bubbleNum =
          this.$themeConfig.indexImg.bubbleNum == undefined
            ? this.bubbleNum
            : this.$themeConfig.indexImg.bubbleNum;
      }
    },
    // ÁÇπÂáªÁÆ≠Â§¥Âêë‰∏ãÊªëÂä®
    scrollFn() {
      const windowH = document.getElementsByClassName(banner)[0].clientHeight; // Ëé∑ÂèñÁ™óÂè£È´òÂ∫¶
      window.scrollTo({
        top: windowH,
        behavior: "smooth", // Âπ≥ÊªëÊªöÂä®
      });
    },
    // Ëß¶Âèë‰∏ãÊãâÊåâÈíÆ
    clickArrow() {
      const arrow = document.getElementById("banner-arrow");
      arrow.click();
    },
    // ÁõëÂê¨È°µÈù¢ÊªöÂä®ÁöÑÂõûË∞É
    watchScroll() {
      const windowH = document.getElementsByClassName(banner)[0].clientHeight; // Ëé∑ÂèñÁ™óÂè£È´òÂ∫¶
      window.onscroll = () => {
        if (document.documentElement.scrollTop < windowH) {
          this.blurText(this.navColor);
          this.noBgBlur();
        } else {
          if (this.switchNavColor && this.navColor == 1) {
            this.blurText(2);
          } else if (this.switchNavColor && this.navColor == 2) {
            this.blurText(1);
          }
          this.bgBlur();
        }
      };
    },
    // ÂØºËà™Ê†èÊÅ¢Â§çÂéü‰∏ªÈ¢òÊ†∑Âºè
    bgBlur() {
      let navbar = document.getElementsByClassName("navbar")[0];
      navbar.className = "navbar blur";
    },
    // ÂØºËà™Ê†èÈÄèÊòé
    noBgBlur() {
      let navbar = document.getElementsByClassName("navbar")[0];
      navbar.className = "navbar navbar1 blur";
    },
    // ÂØºËà™Ê†èÁöÑÂ≠ó‰ΩìÈ¢úËâ≤
    blurText(navColor = this.navColor) {
      let title = document.getElementsByClassName("site-name")[0];
      let search = document.getElementsByClassName("search-box")[0];
      let nav = document.getElementsByClassName("nav-links")[0];
      if (navColor == 1) {
        title.className = "site-name can-hide";
        nav.className = "nav-links can-hide";
        search.className = "search-box";
      } else if (navColor == 2) {
        title.className = "site-name site-name1 can-hide";
        nav.className = "nav-links nav-links1 can-hide";
        search.className = "search-box search-box1";
      }
    },
    // ËÉåÊôØËâ≤ÈöèÊó∂Èó¥ÂèòÂåñÔºåÊó∂Èó¥ÊèêÁ§∫Ê°ÜÂÜÖÂÆπÈöèÊó∂Èó¥ÂèòÂåñ
    bgTimeColorAndTip() {
      var hours = new Date().getHours();
      var minutes = new Date().getMinutes();
      var seconds = new Date().getSeconds();
      hours = hours < 10 ? "0" + hours : hours;
      minutes = minutes < 10 ? "0" + minutes : minutes;
      seconds = seconds < 10 ? "0" + seconds : seconds;
      let div = document.createElement("div");
      div.className = "banner-color";
      if (hours >= 6 && hours < 11) {
        div.style.backgroundColor = this.bgTimeColorArray[0];
        addTip(
          `Êó©‰∏äÂ•ΩÂëÄ~~ÔºåÁé∞Âú®ÊòØ ${hours}:${minutes}:${seconds}ÔºåÂêÉÊó©È§ê‰∫ÜÂêóÔºüüòäü§≠`,
          "info",
          50,
          4000
        );
      } else if (hours >= 12 && hours <= 16) {
        div.style.backgroundColor = this.bgTimeColorArray[0];
        addTip(
          `‰∏ãÂçàÂ•ΩÂëÄ~~ÔºåÁé∞Âú®ÊòØ ${hours}:${minutes}:${seconds}ÔºåÁπÅÂøôÁöÑ‰∏ãÂçà‰πüË¶ÅÈÄÇÂΩì‰ºëÊÅØÂì¶ü•§üèÄ~~`,
          "info",
          50,
          4000
        );
      } else if (hours >= 16 && hours <= 19) {
        div.style.backgroundColor = this.bgTimeColorArray[1];
        addTip(
          `Âà∞ÈªÑÊòè‰∫Ü~~ÔºåÁé∞Âú®ÊòØ ${hours}:${minutes}:${seconds}ÔºåËØ•ÂáÜÂ§áÂêÉÈ•≠Âï¶ü•óüçñ~~`,
          "info",
          50,
          4000
        );
      } else if (hours >= 19 && hours < 24) {
        div.style.backgroundColor = this.bgTimeColorArray[2];
        addTip(
          `Êôö‰∏äÂ•ΩÂëÄ~~ÔºåÁé∞Âú®ÊòØ ${hours}:${minutes}:${seconds}ÔºåËØ•ÂáÜÂ§áÊ¥óÊº±Áù°ËßâÂï¶ü•±üò™~~`,
          "info",
          50,
          4000
        );
      } else if (hours >= 0 && hours < 6) {
        div.style.backgroundColor = this.bgTimeColorArray[3];
        addTip(
          `Âà´ÂÜçÁÜ¨Â§ú‰∫Ü~~ÔºåÁé∞Âú®ÊòØ ${hours}:${minutes}:${seconds}ÔºåÊó©ÁÇπÁù°ÂêßÔºåËÆ©Êàë‰ª¨‰∏ÄËµ∑Ê¨£ËµèÊó©‰∏äÁöÑÂ§™Èò≥~~üòáüõè`,
          "info",
          50,
          4000
        );
      }
      document.getElementsByClassName(banner)[0].parentNode.append(div);
    },
    // Â≠ó‰ΩìÊ∑°ÂÖ•Ê∑°Âá∫
    textFadeInAndOut(
      desc = this.desc, // ÊñáÂ≠óÊèèËø∞
      descFontSize = this.descFontSize, // Â≠ó‰ΩìÂ§ßÂ∞è
      descFadeInTime = this.descFadeInTime, // Ê∑°ÂÖ•Êó∂Èó¥
      descFadeOutTime = this.descFadeOutTime, // Ê∑°Âá∫Êó∂Èó¥
      descNextTime = this.descNextTime // ‰∏ã‰∏Ä‰∏™ÊèèËø∞Âá∫Áé∞Êó∂Èó¥
    ) {
      let descElement = document.getElementsByClassName("description")[0];

      if (descElement) {
        // ÈùûÈ¶ñÈ°µ‰∏çËß¶Âèë
        descElement.style.fontSize = descFontSize;
        var span = document.createElement("span"); // ÂàõÂª∫ | ÁöÑÂÖÉÁ¥†
        span.className = "typed";
        span.innerHTML = "|";
        var index = 0; // ‰∏∫ desc ÁöÑÈïøÂ∫¶ÊúçÂä°
        var length = 0; // ‰∏∫Êï∞ÁªÑÊúçÂä°
        var description = descElement.innerText; // ÂÖàÂèñÈªòËÆ§ÂÄº
        descElement.innerText = ""; // Ê∏ÖÁ©∫ desc
        descElement.appendChild(document.createElement("span")); // ÂàõÂª∫ desc ÊâÄÂú®ÁöÑÊñ∞ÂÖÉÁ¥†
        span && descElement.appendChild(span); // Ê∑ªÂä† | ÁöÑÂÖÉÁ¥†
        // ÂàùÂßãÂåñËø≠‰ª£
        this.fadeInInterval = setInterval(() => {
          fadeIn();
        }, descFadeInTime);
      } else {
        let hero = document.getElementsByClassName("hero")[0];
        descElement = document.createElement("p");
        descElement && (descElement.className = "description");
        descElement && hero.appendChild(descElement);
      }
      // Ê∑°ÂÖ•ÂõûË∞É
      let fadeIn = () => {
        if (descElement) {
          span.style.animation = "none"; // Ê∑°ÂÖ•Êó∂Ôºå| ÂÖâÊ†á‰∏çÂÖÅËÆ∏Èó™ÁÉÅ
          if (desc instanceof Array && desc.length > 0) {
            // Â¶ÇÊûúÊòØ themeConfig ‰º†Êù•ÁöÑÊï∞ÁªÑ
            description = desc[length];
          }
          descElement.firstChild.innerText = description.substring(0, index++);
          if (index > description.length) {
            clearInterval(this.fadeInInterval);
            span.style.animation = "typedBlink 1s infinite"; // Ê∑°ÂÖ•ÁªìÊùüÔºå| ÂÖâÊ†áÂÖÅËÆ∏Èó™ÁÉÅ
            setTimeout(() => {
              this.fadeOutInterval = setInterval(() => {
                fadeOut();
              }, descFadeOutTime);
            }, descNextTime);
          }
        }
      };
      // Ê∑°Âá∫ÂõûË∞É
      let fadeOut = () => {
        if (index >= 0) {
          span.style.animation = "none"; // Ê∑°Âá∫Êó∂Ôºå| ÂÖâÊ†á‰∏çÂÖÅËÆ∏Èó™ÁÉÅ
          descElement.firstChild.innerText = description.substring(0, index--);
        } else {
          clearInterval(this.fadeOutInterval);
          span.style.animation = "typedBlink 1s infinite"; // Ê∑°Âá∫ÁªìÊùüÔºå| ÂÖâÊ†áÂÖÅËÆ∏Èó™ÁÉÅ
          setTimeout(() => {
            length++;
            if (length >= desc.length) {
              length = 0; // desc Â±ïÁ§∫ÂÆåÔºåÈáçÊñ∞ÂºÄÂßãËÆ°Êï∞
            }
            this.fadeInInterval = setInterval(() => {
              fadeIn();
            }, descFadeInTime);
          }, descNextTime);
        }
      };
    },
    // Ê∞îÊ≥°ÊïàÊûú
    canvasBubble(bubbleNum = this.bubbleNum) {
      var canvas = document.getElementById("canvas");
      var cxt = canvas.getContext("2d");
      function Dot() {
        this.alive = true;
        this.x = Math.round(Math.random() * canvas.width);
        this.y = Math.round(Math.random() * canvas.height);
        this.diameter = Math.random() * 10.8;
        this.ColorData = {
          Red: Math.round(Math.random() * 255),
          Green: Math.round(Math.random() * 255),
          Blue: Math.round(Math.random() * 255),
        };
        this.alpha = 0.5;
        this.color =
          "rgba(" +
          this.ColorData.Red +
          ", " +
          this.ColorData.Green +
          "," +
          this.ColorData.Blue +
          "," +
          this.alpha +
          ")";
        this.velocity = {
          x: Math.round(Math.random() < 0.5 ? -1 : 1) * Math.random() * 0.7,
          y: Math.round(Math.random() < 0.5 ? -1 : 1) * Math.random() * 0.7,
        };
      }
      Dot.prototype = {
        Draw: function () {
          cxt.fillStyle = this.color;
          cxt.beginPath();
          cxt.arc(this.x, this.y, this.diameter, 0, Math.PI * 2, false);
          cxt.fill();
        },
        Update: function () {
          if (this.alpha < 0.8) {
            this.alpha += 0.01;
            this.color =
              "rgba(" +
              this.ColorData.Red +
              ", " +
              this.ColorData.Green +
              "," +
              this.ColorData.Blue +
              "," +
              this.alpha +
              ")";
          }
          this.x += this.velocity.x;
          this.y += this.velocity.y;
          if (
            this.x > canvas.width + 5 ||
            this.x < 0 - 5 ||
            this.y > canvas.height + 5 ||
            this.y < 0 - 5
          ) {
            this.alive = false;
          }
        },
      };
      var Event = {
        rArray: [],
        Init: function () {
          canvas.width = window.innerWidth;
          canvas.height = window.innerHeight;
          for (var x = 0; x < bubbleNum; x++) {
            this.rArray.push(new Dot());
          }
          this.Update();
        },
        Draw: function () {
          cxt.clearRect(0, 0, canvas.width, canvas.height);
          this.rArray.forEach(function (dot) {
            dot.Draw();
          });
        },
        Update: function () {
          if (Event.rArray.length < bubbleNum) {
            for (var x = Event.rArray.length; x < bubbleNum; x++) {
              Event.rArray.push(new Dot());
            }
          }
          Event.rArray.forEach(function (dot) {
            dot.Update();
          });
          Event.rArray = Event.rArray.filter(function (dot) {
            return dot.alive;
          });
          Event.Draw();
          requestAnimationFrame(Event.Update);
        },
      };
      window.onresize = function () {
        Event.rArray = [];
        canvas.width = window.innerWidth;
        canvas.height = window.innerHeight;
      };
      Event.Init();
    },
  },
  // Èò≤Ê≠¢ÈáçÂÜôÁºñËØëÊó∂ÔºåÂØºËá¥ÂÆöÊó∂Âô®Âè†Âä†ÈóÆÈ¢ò
  beforeMount() {
    clearInterval(this.fadeInInterval);
    clearInterval(this.fadeOutInterval);
  },
  beforeDestroy() {
    clearInterval(this.fadeInInterval);
    clearInterval(this.fadeOutInterval);
  },
};
/**
 * Ê∑ªÂä†Ê∂àÊÅØÊèêÁ§∫
 * contentÔºöÂÜÖÂÆπ
 * typeÔºöÂºπÁ™óÁ±ªÂûãÔºàtip„ÄÅsuccess„ÄÅwarning„ÄÅdangerÔºâ
 * startHeightÔºöÁ¨¨‰∏Ä‰∏™ÂºπÁ™óÁöÑÈ´òÂ∫¶ÔºåÈªòËÆ§ 50
 * dieTimeÔºöÂºπÁ™óÊ∂àÂ§±Êó∂Èó¥ÔºàÊØ´ÁßíÔºâÔºåÈªòËÆ§ 3000 ÊØ´Áßí
 *
 * Âú® head ÈáåÊ∑ªÂä†ÂõæÊ†á link Âú∞ÂùÄÔºöhttps://at.alicdn.com/t/font_3114978_qe0b39no76.css
 */
function addTip(content, type, startHeight = 50, dieTime = 3000) {
  var tip = document.querySelectorAll(".global-tip");
  var time = new Date().getTime();
  // Ëé∑ÂèñÊúÄÂêéÊ∂àÊÅØÊèêÁ§∫ÂÖÉÁ¥†ÁöÑÈ´òÂ∫¶
  var top = tip.length == 0 ? 0 : tip[tip.length - 1].getAttribute("data-top");
  // Â¶ÇÊûú‰∫ßÁîü‰∏§‰∏™‰ª•‰∏äÁöÑÊ∂àÊÅØÊèêÁ§∫ÔºåÂàôÂá∫Áé∞Âú®‰∏ä‰∏Ä‰∏™ÊèêÁ§∫ÁöÑ‰∏ãÈù¢ÔºåÂç≥È´òÂ∫¶Ê∑ªÂä†ÔºåÂê¶ÂàôÈªòËÆ§ 50
  var lastTop =
    parseInt(top) +
    (tip.length != 0 ? tip[tip.length - 1].offsetHeight + 17 : startHeight);

  let div = document.createElement("div");
  div.className = `global-tip tip-${type} ${time}`;
  div.style.top = parseInt(top) + "px";
  div.setAttribute("data-top", lastTop);
  if (type == "info" || type == 1) {
    div.innerHTML = `<i class="iconfont icon-info icon"></i><p class="tip-info-content">${content}</p>`;
  } else if (type == "success" || type == 2) {
    div.innerHTML = `<i class="iconfont icon-dagouyouquan icon"></i><p class="tip-success-content">${content}</p>`;
  } else if (type == "danger" || type == 3) {
    div.innerHTML = `<i class="iconfont icon-cuowu icon"></i><p class="tip-danger-content">${content}</p>`;
  } else if (type == "warning" || type == 4) {
    div.innerHTML = `<i class="iconfont icon-gantanhao icon"></i><p class="tip-warning-content">${content}</p>`;
  }
  document.body.appendChild(div);

  let timeTip = document.getElementsByClassName(time)[0];
  setTimeout(() => {
    timeTip.style.top = parseInt(lastTop) + "px";
    timeTip.style.opacity = "1";
  }, 10);

  // Ê∂àÊÅØÊèêÁ§∫ dieTime ÁßíÂêéÈöêËóèÂπ∂Ë¢´Âà†Èô§
  setTimeout(() => {
    timeTip.style.top = "0px";
    timeTip.style.opacity = "0";

    // ‰∏ãÈù¢ÁöÑÊâÄÊúâÂÖÉÁ¥†ÂõûÂà∞ÂêÑËá™ÊõæÁªèÁöÑÂá∫ÂèëÁÇπ
    var allTipElement = nextAllTipElement(timeTip);
    for (let i = 0; i < allTipElement.length; i++) {
      var next = allTipElement[i];
      var top =
        parseInt(next.getAttribute("data-top")) - next.offsetHeight - 17;
      next.setAttribute("data-top", top);
      next.style.top = top + "px";
    }
    setTimeout(() => {
      timeTip.remove();
    }, 500);
  }, dieTime);
}
/**
 * Ëé∑ÂèñÂêéÈù¢ÁöÑÂÖÑÂºüÂÖÉÁ¥†
 */
function nextAllTipElement(elem) {
  var r = [];
  var n = elem;
  for (; n; n = n.nextSibling) {
    if (n.nodeType === 1 && n !== elem) {
      r.push(n);
    }
  }
  return r;
}
